import React from "react";

const Errors = props => {
  const { errorcode } = props;

  const styles = {
    box: {
      backgroundColor: "red",
      height: "800px",
      width: "1280px"
    },
    text: {
      justifyText: "center",
      alignText: "center"
    }
  };
  let error = "Error";

  if (errorcode === 1) {
    error = "No water";
  } else if (errorcode === 2 || 3) {
    error = "Machine is broken";
  }

  const errormessage = `Unfortunately there is a technical error: ${error}. Making drinks is not possible at this time`;

  const isError =
    errorcode === 0 ? null : (
      <div style={styles.box}>
        <h1 style={styles.text}>{errormessage}</h1>
      </div>
    );

  return isError;
};

export default Errors;
